<?php

namespace News\CoreBundle\Repository;

use Doctrine\ORM\QueryBuilder;
use Doctrine\ORM\EntityRepository;
use News\CoreBundle\Entity\Comment;

/**
 * CommentRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class CommentRepository extends EntityRepository
{
    /**
     * Finds recent comments.
     *
     * @param int $num
     *
     * @return array
     */
    public function findLatest($num)
    {
        $qb = $this->getQueryBuilder()->orderBy('c.id', 'desc')->setMaxResults($num);

        return $qb->getQuery()->getResult();
    }

    /**
     * Gets a new QueryBuilder instance.
     *
     * @return QueryBuilder
     */
    private function getQueryBuilder()
    {
        $em = $this->getEntityManager();

        return $em->getRepository('NewsCoreBundle:Comment')->createQueryBuilder('c');
    }
}
